import React, { Component } from 'react';
import './email-form.css';
import { Typography, Button } from '@material-ui/core';
import $ from 'jquery';

class EmailForm extends Component {
  sendEmail() {
    console.log('sending the email!!');
    let name = $('#contact-name')[0];
    let email = $('#contact-email')[0];
    let subject = $('#contact-subject')[0];
    let body = $('#contact-email-body')[0];
    console.log(name, email, subject, body)
  }

  render() {
    return (
      <div className="page-container">
        <Typography variant='h6'>
          Contact Form:
        </Typography>
        <br/>
        <form>
          <label htmlFor="contact-name">
            Name
          </label><br/>
          <input type="text" id="contact-name" name="contact-name"/><br/><br/>

          <label htmlFor="contact-email">
            Email Address
          </label><br/>
          <input type="email" id="contact-email" name="contact-email"/><br/><br/>

          <label htmlFor="contact-subject">
            Subject
          </label><br/>
          <input id="contact-subject" name="contact-subject"/><br/><br/>

          <label htmlFor="contact-email-body">
            Email Body
          </label><br/>
          <textarea id="contact-email-body" name="contact-email-body"/><br/><br/>

          <Button onClick={this.sendEmail}>Send</Button>
        </form>
      </div>
    );
  }
}

export default EmailForm;
