import React, { Component } from 'react';

/**
 * DropdownMenu Class
 * In case I want to cry over muicss dropdowns instead of my own, better-behaved
 * implementation: https://www.muicss.com/docs/v1/react/dropdowns
 * Or Boostrap's dropdown, even tho I'm using MUI framework:
 * https://react-bootstrap.github.io/components/dropdowns/
 */
class DropdownMenu extends Component {
  constructor(props) {
    super(props);
    this.linkTuples = props.linkTuples; // [title, href]
  }

  /**
   * Render the Navbar component
   * @returns {ReactElement}
   */
  render() {
    // let linksUL = (
    //   <ul className="navbar-dropdown mui-dropdown__menu mui--is-open" style={{top: '39.5px'}}>
    //     <li className="navbar-dropdown" style={{backgroundColor: 'indianred'}}>
    //       <a href="mylinks.html" className="navbar-dropdown">My Links</a>
    //     </li>
    //     <li className="navbar-dropdown" style={{backgroundColor: 'indianred'}}>
    //       <a href="blmlinks.html" className="navbar-dropdown">BLM Links</a>
    //     </li>
    //   </ul>
    // );

    let ul = document.createElement('ul');
    ul.className = 'navbar-dropdown mui-dropdown__menu mui--is-open';
    ul.style.top = '39.5px';

    this.linkTuples.foreach((linkTuple) => {
      let a = document.createElement('li');
      a.className = 'navbar-dropdown';
      a.innerText = linkTuple[0];
      a.href = linkTuple[1];

      let li = document.createElement('li');
      li.className = 'navbar-dropdown';
      li.style.backgroundColor = 'indianred';
      li.appendChild(a);

      ul.appendChild(li);
    })

    return ul;
  }
}

export default DropdownMenu;
